mapper.select:
select count(1) from ( select t1.*,t2.category_name ,t4.discount ,t4.discount from tb_product t1 left join tb_product_category t2 on t1.category_id = t2.id
left join dms.tb_dealer_auth_product t3 on t3.product_category_id = t2.id left join dms.tb_dealer_auth_discount t4 on t1.item_number = t4.item_number and t4.dealer_id = t3.dealer_id where t3.dealer_id = #{dealer_id}
left join dms.tb_dealer_auth_product t3 on t3.product_category_id = t2.id left join dms.tb_dealer_auth_discount t4 on t1.item_number = t4.item_number and t4.dealer_id = t3.dealer_id where t3.dealer_id in (${dealer_ids})
) t

select * from ( select t1.*,t2.category_name ,t4.discount ,t4.discount from tb_product t1 left join tb_product_category t2 on t1.category_id = t2.id
left join dms.tb_dealer_auth_product t3 on t3.product_category_id = t2.id left join dms.tb_dealer_auth_discount t4 on t1.item_number = t4.item_number and t4.dealer_id = t3.dealer_id where t3.dealer_id = #{dealer_id}
left join dms.tb_dealer_auth_product t3 on t3.product_category_id = t2.id left join dms.tb_dealer_auth_discount t4 on t1.item_number = t4.item_number and t4.dealer_id = t3.dealer_id where t3.dealer_id in (${dealer_ids})
) t
limit #{offset},#{pagesize}
